import * as react_router from 'react-router';
import { MiddlewareFunction } from 'react-router';
import { AuthObject } from '@clerk/backend';
import { RequestState } from '@clerk/backend/internal';
import { PendingSessionOptions } from '@clerk/types';
import { ClerkMiddlewareOptions } from './types.js';

declare const authFnContext: react_router.RouterContext<((options?: PendingSessionOptions) => AuthObject) | null>;
declare const requestStateContext: react_router.RouterContext<RequestState<any> | null>;
/**
 * Middleware that integrates Clerk authentication into your React Router application.
 * It checks the request's cookies and headers for a session JWT and, if found,
 * attaches the Auth object to a context.
 *
 * @example
 * // react-router.config.ts
 * export default {
 *   future: {
 *     v8_middleware: true,
 *   },
 * }
 *
 * // root.tsx
 * export const middleware: Route.MiddlewareFunction[] = [clerkMiddleware()]
 */
declare const clerkMiddleware: (options?: ClerkMiddlewareOptions) => MiddlewareFunction<Response>;

export { authFnContext, clerkMiddleware, requestStateContext };
